mapper.update:

INSERT INTO fp_alim (
	  grp
	, alimType
	, title
	, content
	, linkUrl
	, linkImageUrl
	, receiverIdKey
	, receiveDate
	, senderIdKey
	, sendDate
) VALUES ( 
	  #{grp}
	, #{alimType}
	, #{title}
	, #{content}
	, #{linkUrl}
	, #{linkImageUrl}
	, #{receiverIdKey}
	, null
	, #{senderIdKey}
	, now()
)


UPDATE fp_alim SET
	  receiveDate = now()
WHERE idKey = #{idKey}


INSERT INTO fp_alim_setting (
	  userIdKey
	, myLookComment
	, myCommentComent
	, myCoolComment
	, followMe
	, coolMyLook
	, appNotice
	, appEvent
) VALUES ( 
	  #{userIdKey}
	, #{myLookComment}
	, #{myCommentComent}
	, #{myCoolComment}
	, #{followMe}
	, #{coolMyLook}
	, #{appNotice}
	, #{appEvent}
)


UPDATE fp_alim_setting SET 
	  myLookComment		= #{myLookComment}
	, myCommentComent	= #{myCommentComent}
	, myCoolComment		= #{myCoolComment}
	, followMe			= #{followMe}
	, coolMyLook		= #{coolMyLook}
	, appNotice			= #{appNotice}
	, appEvent			= #{appEvent}
WHERE userIdKey = #{userIdKey}
