mapper.update:
UPDATE fp_user_model_map map
SET map.useYn = #{useYn}
WHERE map.modelKey = #{modelKey}
AND map.userKey = ${idKey}

UPDATE fp_user_profile
nickName=#{nickName}, content=#{content}, imageUrl = #{imageUrl},
WHERE userIdKey = #{idKey}

mapper.select:
		SELECT count(*)
  		  FROM fp_follow
 		 WHERE userIdKey = #{userIdKey} AND followUserIdKey = #{followUserIdKey}

SELECT f.userIdKey ,f.followUserIdKey ,u.userId ,up.imageUrl
,( SELECT count(*) FROM fp_follow WHERE userIdKey = #{sessionIdKey}
AND followUserIdKey = u.idKey
AND followUserIdKey = f.followUserIdKey
) isFollow
FROM fp_follow f LEFT JOIN fp_user u ON
f.userIdKey = u.idKey
f.followUserIdKey = u.idKey
LEFT JOIN fp_user_profile up ON u.idKey = up.userIdKey WHERE 1=1 AND u.userId LIKE CONCAT('%', #{searchId},'%')
AND f.followUserIdKey = #{idKey}
AND f.userIdKey = #{idKey}
ORDER BY f.followDate desc
LIMIT #{pageStart}, #{pageSize}

SELECT (SELECT COUNT(*) 
 		  FROM fp_follow f
 		  JOIN fp_user u ON f.userIdKey = u.idKey
 		 WHERE followUserIdKey = #{idKey}

	 		   		AND u.userId LIKE CONCAT('%', #{searchId},'%')


 	   ) followerCount
 	  ,(SELECT COUNT(*) 
 		  FROM fp_follow f
 		  JOIN fp_user u ON f.followUserIdKey = u.idKey
 		 WHERE userIdKey = #{idKey}

	 		   		AND u.userId LIKE CONCAT('%', #{searchId},'%')

 	   ) followingCount

mapper.update:
UPDATE fp_user_model
userKey=#{userKey}, seq=#{seq}, modelName=#{modelName}, gender=#{gender}, height=#{height}, age=#{age}, fit=#{fit}, bra=#{bra}, brb=#{brb}, arms=#{arms}, forearm=#{forearm}, hip=#{hip}, leg=#{leg}, lowerSize=#{lowerSize}, shoulder=#{shoulder}, thigh=#{thigh}, upperSize=#{upperSize}, waist=#{waist}, imgUrl=#{imgUrl}, fitFr=#{fitFr}, braFr=#{braFr}, brbFr=#{brbFr}, armsFr=#{armsFr}, forearmFr=#{forearmFr}, hipFr=#{hipFr}, legFr=#{legFr}, lowerSizeFr=#{lowerSizeFr}, shoulderFr=#{shoulderFr}, thighFr=#{thighFr}, upperSizeFr=#{upperSizeFr}, waistFr=#{waistFr}, heightFr=#{heightFr}, sizeType=#{sizeType},
WHERE idKey = #{idKey}
