mapper.update:
UPDATE FP_Notice
   SET viewCount = viewCount+1
 WHERE idKey = #{idKey}

mapper.insert:
INSERT into FP_Notice(
	  userIdKey
	, createDate
	, title
	, content
	, viewCount
	, startDate
	, endDate
) VALUES (
	  #{userIdKey}
	, now()
	, #{title}
	, #{content}
	, 0
	, #{startDate}
	, #{endDate}
)

mapper.update:
UPDATE FP_Notice
   SET   createDate = now()
   	   , title = #{title}
   	   , content = #{content}
   	   , startDate = #{startDate}
   	   , endDate = #{endDate}
 WHERE idKey = #{idKey}
