mapper.update:

INSERT INTO fp_review (
	  lookIdKey
	, clickKey
	, userIdKey
	, productIdKey
	, reviewName
	, reviewMsg
	, reviewDate
) VALUES (
	  #{lookIdKey}
	, #{clickKey}
	, #{userIdKey}
	, #{productIdKey}
	, #{reviewName}
	, #{reviewMsg}
	, now()
)

	UPDATE fp_review SET 
		  reviewName		= #{reviewName}
		, reviewMsg			= #{reviewMsg}
    WHERE idKey		 		= #{reviewIdKey}

mapper.select:
SELECT pm.lookIdKey , rv.idKey as reviewIdKey , pc.mapIdKey , pc.clickKey , pm.producIdKey as productIdKey , pm.productName , pc.createDate , pc.userIdKey , pc.settleYn , pc.rewardYn , uo.userId as lookUserId , up.imageUrl as userImageUrl , pd.price , ur.userId
, rv.reviewName , rv.reviewMsg , rv.reviewDate
, pm.useYn as mapUseYn , pd.salesStatus FROM fp_look_product_click pc inner join fp_review rv on pc.clickKey = rv.clickKey inner join fp_look_porduct_map pm on pc.mapIdKey = pm.idKey inner join fp_user ur on pc.userIdKey = ur.idKey /*구매자*/ left outer join fp_user_profile up on pc.userIdKey = up.userIdKey /*구매자*/ left outer join fp_look lk on pm.lookIdKey = lk.idKey left outer join fp_user uo on lk.userIdKey = uo.idKey /*판매자*/ left outer join fp_product pd on pc.pcode = pd.pcode WHERE rv.idKey = #{reviewIdKey}

mapper.insert:

INSERT INTO fp_review_image (
	  reviewIdKey
	, seq
	, imageAlt
	, imageUrl
	, imageSrc
) VALUES ( 
	  #{reviewIdKey}
	, #{seq}
	, #{imageAlt}
	, #{imageUrl}
	, #{imageSrc}
)
