mapper.select:
SELECT idKey ,userId ,password ,userType ,mobile ,email ,lastLoginDate ,joinDate ,snsType ,snsId ,deviceId ,status ,adminYn FROM fp_user WHERE 1 = 1
AND idKey = #{idKey}
AND userId = #{userId}
AND password = #{password}
AND mobile = #{mobile}

SELECT	COUNT(*) AS count
FROM	fp_user_filter
WHERE	deviceId =  #{deviceId}
AND		gender = #{gender}
AND		country = #{country}

mapper.update:
UPDATE fp_user
   SET lastLoginDate = now()
 WHERE idKey = #{_parameter}

UPDATE fp_user_filter
   SET userIdKey = #{idKey}
 WHERE idKey = #{userFilterIdKey}

UPDATE fp_user_filter
   SET country = #{country}
 WHERE userIdKey = #{idKey}

mapper.select:
SELECT idKey , modelName , userKey , seq , height , age , fit , bra1 , bra2 , bottom FROM fp_user_model lc WHERE 1 = 1
AND idKey = #{idKey}
AND userKey = #{userKey}
ORDER BY lc.seq

mapper.update:
UPDATE fp_user SET 
	  status			= '9'
	, leaveReason		= #{leaveReason}
 WHERE idKey = #{idKey}
