mapper.select:
SELECT idKey ,userId ,password ,userType ,mobile ,email ,lastLoginDate ,joinDate ,snsType ,snsId ,deviceId ,status FROM FP_User WHERE 1 = 1
AND idKey = #{idKey}
AND userId = #{userId}
AND password = #{password}
AND mobile = #{mobile}

mapper.insert:
INSERT into FP_User(
	 userId
	,password
	,userType
	,mobile
	,email
	,joinDate
	,deviceId
	,status
) VALUES (
	  #{userId}
	, #{password}
	, #{userType}
	, #{mobile}
	, #{email}
	, now()
	, #{deviceId}
	, #{status}
)
